import React, {Component} from 'react';
import {BrowserRouter, Route} from "react-router-dom";

import Home from './pages/Home';
import Navbar from './components/Navbar';
import Begeleiding from './pages/Begeleiding';
import Professionals from './pages/Professionals';
import Informatie from './pages/Informatie';
import Contact from './pages/Contact';
import Axios from 'axios';
import API_ENDPOINT from "./config";
import Login from "./pages/Login";
import MoreInfo from "./pages/MoreInfo";
import Admin from "./pages/Admin";
import User from "./pages/User";
import PrivateRoute from "./components/PrivateRoute";
import ProfessionalForm from "./pages/Admin/ProfessionalForm";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            professionals: [],
            filteredProfessional: [],
            selectedProfessional: {}
        }
    }

    handleProfessionalsChanged = (professionals) => {
        this.setState({filteredProfessional: professionals});
    };

    handleSelectedProfessionalChanged = (professional) => {
        this.setState({selectedProfessional: professional});
    };

    componentDidMount() {
        Axios.get(`${API_ENDPOINT}/professionals`)
            .then(response => {
                const {data} = response;
                const professionals = Object.values(data);
                this.setState({professionals, filteredProfessional: professionals});
            })
            .catch(err => {
                console.log(err);
            });
    }

    render() {
        return (
            <BrowserRouter>
                <div className="App">
                    <Navbar/>
                    <Route exact path='/' render={(props) => (
                        <Home onProfessionalsChanged={this.handleProfessionalsChanged}
                              professionals={this.state.professionals}
                              filteredProfessionals={this.state.filteredProfessional}
                              {...props}
                        />
                    )}/>
                    <Route path='/professionals' render={() => (
                        <Professionals onProfessionalsChanged={this.handleProfessionalsChanged}
                                       onSelectedProfessionalChanged={this.handleSelectedProfessionalChanged}
                                       professionals={this.state.professionals}
                                       filteredProfessionals={this.state.filteredProfessional}/>
                    )}/>

                    <Route path='/login' component={Login}/>
                    <PrivateRoute path='/user' component={User}/>
                    <PrivateRoute path='/admin' component={Admin}/>
                    <Route path='/begeleiding' component={Begeleiding}/>
                    <Route path='/informatie' component={Informatie}/>
                    <Route path='/contact' component={Contact}/>
                    <Route path='/more-info' render={(props) => (
                        <MoreInfo
                            professional={this.state.selectedProfessional}
                            {...props}
                        />
                    )}/>
                    <Route path='/form' component={ProfessionalForm}/>


                </div>
            </BrowserRouter>
        );
    }
}

export default App;
