import React, {Component} from 'react';
import {NavLink} from "react-router-dom";
import ProfessionalFilterFunction from "./ProfessionalFilterFunction";

class ProfessionalsFilters extends Component {

    state = {
        currentType: null
    };

    selectProfessionalsType = (type) => {

        if (this.state.currentType === null) {
            this.setState({currentType: type});
        } else {
            if (this.state.currentType === type) {
                this.setState({currentType: null});
            }
        }
        // go through a list of all professionals and match their type with the given type. if they match they may stay if they don't they will be deleted.
        // It is only possible to select one type at the time.
        const filteredProfessionals = this.props.professionals.filter(professional => professional.therapy_type.name === type);
        this.props.onProfessionalsChanged(filteredProfessionals);
    };

    render() {

        return (
            <div className="ProfessionalsFiltersContainer">
                {console.log("cur type: ", this.state.currentType)}
                <div className="filterHeadingContainer">
                    <h1 className="filterHeader">Kies je professional</h1>
                </div>

                <div className="filterIconsContainer">

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Fysiotherapie')} className={`imageAndTextContainer ${this.state.currentType === 'Fysiotherapie'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/fysiotherapie.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Fysiotherapie</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Oefentherapie')} className={`imageAndTextContainer ${this.state.currentType === 'Oefentherapie'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/oefentherapie.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Oefentherapie</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Logopedie')} className={`imageAndTextContainer ${this.state.currentType === 'Logopedie'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/logopedist.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Logopedie</p>
                        </div>
                    </div>

                    <div className="IconContainer" >
                        <div onClick={this.selectProfessionalsType.bind(this, 'Ergotherapie')} className={`imageAndTextContainer ${this.state.currentType === 'Ergotherapie'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/ergotherapie.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Ergotherapie</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Dietist')} className={`imageAndTextContainer ${this.state.currentType === 'Dietist'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/dietist.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Dietist</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Revalidatie')} className={`imageAndTextContainer ${this.state.currentType === 'Revalidatie'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/revalidatie.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Revalidatie</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Zorguitleen')} className={`imageAndTextContainer ${this.state.currentType === 'Zorguitleen'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/zorguitleen.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Zorguitleen</p>
                        </div>
                    </div>

                    <div className="IconContainer">
                        <div onClick={this.selectProfessionalsType.bind(this, 'Ziekenhuizen')} className={`imageAndTextContainer ${this.state.currentType === 'Ziekenhuizen'  ? 'selected' : ''}`}>
                            <div className="filterIconWrapper">
                                <img src={require('../../src/assets/icons/New icons-CVA/ziekenhuizen.svg')}
                                     className="filterImage"/>
                            </div>
                            <p className="filterTitle">Ziekenhuizen</p>
                        </div>
                    </div>

                </div>

            </div>
        );
    }

};

export default ProfessionalsFilters;
