import React, {Component} from 'react';
import ProfessionalPicture from '../components/ProfessionalPicture';
import ProfessionalInfo from '../components/ProfessionalInfo';

{/* This page displays information about specific professionals */
}

class MoreInfo extends Component {

    componentDidMount() {
        this.FlipArrow();
        document.getElementById("ProfessionalInfo").addEventListener('scroll', () => {
            this.FlipArrow();
        });
    }

    FlipArrow(){
        console.log("evalsbcndsubvdsuvbc");
        let arrowDiv = document.getElementById("ProfessionalInfo");
        if(arrowDiv.scrollTop >= arrowDiv.scrollTop - arrowDiv.clientTop){
            document.getElementById("downscrollImageWrapper").classList.toggle("flip-arrow", false);
            document.getElementById("downscrollImageWrapper").classList.toggle("flip-arrow",true);
            return;
        }

        {/*if(scrollDiv.scrollLeft >= scrollDiv.scrollWidth - scrollDiv.clientWidth){
            document.getElementById("NavArrowWrapperRight").classList.toggle("hide-arrow",true);
            document.getElementById("NavArrowWrapperLeft").classList.toggle("hide-arrow",false);
            return;
        }
        document.getElementById("NavArrowWrapperRight").classList.toggle("hide-arrow", false);
        document.getElementById("NavArrowWrapperLeft").classList.toggle("hide-arrow", false);*/}

    }

    render() {
        if(this.props.professional.id == null){
            // no professional is selected redirect to professional page
            this.props.history.push('/Professionals');
            return null;
        }
        return (
            <div className="MoreInfo">
                <ProfessionalPicture professional={this.props.professional}/>
                <ProfessionalInfo professional={this.props.professional} />

                <div className="downscrollImageWrapper">
                    <img src={require('../assets/icons/downArrow.png')} />
                </div>
            </div>
        )
    }
}

export default MoreInfo;
