import React, {Component} from 'react';
import BottomNavBar from './BottomNavBar';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import PopUpHome from "./PopUpHome";


class Home extends Component {

    state = {
        activeMarker: {},
        selectedPlace: {},
        showingInfoWindow: false
    };

    onMarkerClick = (props, marker) =>
        this.setState({
            activeMarker: marker,
            selectedPlace: props,
            showingInfoWindow: true
        });

    onInfoWindowClose = () =>
        this.setState({
            showingInfoWindow: false,
            activeMarker: {}
        });

    onMapClicked = () => {
        if (this.state.showingInfoWindow)
            this.setState({
                showingInfoWindow: false,
                activeMarker: {}
            });
    };


    render() {
        return (
            <div className="homeBlock">
                <PopUpHome/>
                <div className="map">

                    

                    <Map google={this.props.google} zoom={10} className={'google-maps'}
                         initialCenter={{lat: 52.0057008, lng: 5.826559412}} zoomControl={false}
                         streetViewControl={false}>

                        {/*for loop over all professionals and their lat and long locations*/}
                        <Marker name={'Current location'} position={{lat: 52.0057008, lng: 5.826559412}}
                                onClick={this.onMarkerClick}
                                profName={'professional'}
                                profSpec={'Specialistatie: CVA Therapie'}
                                profStreet={'De straat 12'}
                                profPostal={'1234 AA Arnhem'}/>
                        <InfoWindow marker={this.state.activeMarker}
                                    onClose={this.onInfoWindowClose}
                                    visible={this.state.showingInfoWindow}>
                            <div className={'popup-box-maps'}>
                                <div className={'professionalIcon'}>
                                        <img src={require('../../src/assets/icons/icons/color/logopedist_kleur.svg')} className="icons"/>
                                </div>
                                <div className={'professionalTitle'}>
                                    <p className={'professionalName'}>{this.state.activeMarker.profName}</p>
                                    <p className={'professionalInfo'}>{this.state.activeMarker.profSpec}</p>
                                    <p className={'professionalAdress'}>
                                        {this.state.activeMarker.profStreet} <br/>
                                        {this.state.activeMarker.profPostal}
                                    </p>

                                    <button className={'more-info-button'}>Meer informatie</button>
                                </div>
                            </div>
                        </InfoWindow>
                    </Map>
                    <BottomNavBar/>
                </div>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyCzIMZALyxjW2ZqcdGV7gEFnMceIP3w9B0'
})(Home)
