import React, {Component} from 'react';
import {NavLink} from "react-router-dom";

class MenuOverlay extends Component {

    OpenOverlay = () => {
        this.setState(
            {
                showOverlay: true
            });
    };

    closeOverlay = () => {
        this.setState({
            showOverlay: false
        });
    };

    Overlay = (

        <div id="menuOverlay" className="menuOverlayContainer">

            <div className="crossContainer">
                <span className={'cross'} onClick={this.closeOverlay}>X</span>
            </div>

            <div className="OverlayLinksContainer">
                <div className="OverlayLinks">
                    <NavLink to="/Home" className="OverlayLinks"><p> Home </p></NavLink>
                </div>

                <div className="OverlayLinks">
                    <NavLink to="/Professionals" className="OverlayLinks"><p> Professionals </p></NavLink>
                </div>

                <div className="OverlayLinks">
                    <NavLink to="/Begeleiding" className="OverlayLinks"><p> Begeleiding </p></NavLink>
                </div>

                <div className="OverlayLinks">
                    <NavLink to="/Informatie" className="OverlayLinks"><p> Informatie </p></NavLink>
                </div>

                <div className="OverlayLinks">
                    <NavLink to="Contact" className="OverlayLinks"><p>Contact</p></NavLink>
                </div>
            </div>

        </div>
    );

    render() {
        return (
            this.OpenOverlay ? this.Overlay : null
        )
    }
}

export default MenuOverlay;
