import React, {Component} from 'react';
import {Link, NavLink} from "react-router-dom";

class Navbar extends Component{

    state = {
        showOverlay: false
    };

    openOverlay = () => {
        this.setState({showOverlay: true})
    };

    closeOverlay= () => {
        this.setState({showOverlay: false});
    };

    Overlay =
        (
            <div className="menuOverlayContainer">

                <div className="crossContainer">
                    <span className="cross" onClick={this.closeOverlay}>X</span>
                </div>

                <div className="OverlayLinksContainer">
                    <div className="LinkContainer">
                        <NavLink to="/" className="OverlayLinks" onClick={this.closeOverlay}><p> Home </p></NavLink>
                    </div>

                    <div className="LinkContainer">
                        <NavLink to="/Professionals" className="OverlayLinks" onClick={this.closeOverlay}><p> Professionals </p></NavLink>
                    </div>

                    <div className="LinkContainer">
                        <NavLink to="/Begeleiding" className="OverlayLinks" onClick={this.closeOverlay}><p> Begeleiding </p></NavLink>
                    </div>

                    <div className="LinkContainer">
                        <NavLink to="/Informatie" className="OverlayLinks" onClick={this.closeOverlay}><p> Informatie </p></NavLink>
                    </div>

                    <div id="removeBorder" className="LinkContainer">
                        <NavLink to="/Contact" className="OverlayLinks" onClick={this.closeOverlay}><p>Contact</p></NavLink>
                    </div>
                </div>

                <div className="transparentBackground" onClick={this.closeOverlay}>
                    {/*<a className="closeOverlay" href="#"><p></p></a>*/}
                </div>

            </div>
        );

    render() {

        return (

            <div>

                <div className="navBarContainer">

                    <div className="cvaNavLogoContainer">
                        <Link to="/" className="cvaHome">
                            <div className="image-wrapper2">
                                <img src={require('../assets/images/homelogo.png')}/>
                            </div>
                        </Link>
                    </div>

                    <div className="menuButtonWrapper" onClick={this.openOverlay}>
                        <button className="menuButton">
                            <img src={require('../assets/icons/mobile/button_mobilemenu.png')}/>
                        </button>

                    </div>

                    {this.state.showOverlay ? this.Overlay : null}

                    <div className="buttonListContainer">

                        <div className="buttonContainer">
                            <NavLink to="/Begeleiding" className="cvaNavButtons"> Begeleiding </NavLink>
                        </div>

                        <div className="buttonContainer">
                            <NavLink to="/Professionals" className="cvaNavButtons"> Professionals </NavLink>
                        </div>

                        <div className="buttonContainer">
                            <NavLink to="/Informatie" className="cvaNavButtons"> Informatie </NavLink>
                        </div>

                        <div className="buttonContainerContacts">
                            <NavLink to="/Contact" className="cvaNavButtons"> Contact </NavLink>
                        </div>

                    </div>

                </div>

                <div className="navBarContainer static-push">
                </div>

            </div>
        );
    }
}

export default Navbar;
